        ConfigurableRightsPool crp,
        ERC20 token,
        uint newWeight,
        uint tokenAmountIn
    ) external {
        require(token.transferFrom(msg.sender, address(this), tokenAmountIn), "ERR_TRANSFER_FAILED");
